import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../contexts/AuthContext';
import api from '../../services/api';

const ConferenceCreate = () => {
    const navigate = useNavigate();
    const { user } = useContext(AuthContext);
    const [reviewers, setReviewers] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        date: '',
        reviewerIds: []
    });

    // Fetch available reviewers when component mounts
    useEffect(() => {
        const fetchReviewers = async () => {
            try {
                const response = await api.get('/users/reviewers');
                setReviewers(response.data);
            } catch (err) {
                console.error('Error fetching reviewers:', err);
                setError('Failed to load reviewers. Please try again later.');
            }
        };

        fetchReviewers();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleReviewerChange = (e) => {
        const selectedOptions = Array.from(e.target.selectedOptions, option => option.value);
        setFormData(prev => ({
            ...prev,
            reviewerIds: selectedOptions
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        setSuccess('');

        try {
            // Validate date
            if (new Date(formData.date) < new Date()) {
                setError('Conference date cannot be in the past');
                setLoading(false);
                return;
            }

            // Create conference
            const response = await api.post('/conferences', formData);
            
            setSuccess('Conference created successfully!');
            
            // Redirect to conferences list after short delay
            setTimeout(() => {
                navigate('/conferences');
            }, 2000);

        } catch (err) {
            console.error('Error creating conference:', err);
            setError(err.response?.data?.error || 'Failed to create conference. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    // Protect route - only organizers can create conferences
    if (user?.role !== 'organizer') {
        return (
            <div className="max-w-2xl mx-auto mt-8 p-4">
                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
                    Access denied. Only organizers can create conferences.
                </div>
            </div>
        );
    }

    return (
        <div className="max-w-2xl mx-auto mt-8 p-4">
            <h2 className="text-2xl font-bold mb-6">Create New Conference</h2>
            
            {error && (
                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                    {error}
                </div>
            )}
            
            {success && (
                <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
                    {success}
                </div>
            )}

            <form onSubmit={handleSubmit} className="space-y-6">
                <div>
                    <label htmlFor="title" className="block mb-2 font-medium">
                        Conference Title
                    </label>
                    <input
                        type="text"
                        id="title"
                        name="title"
                        value={formData.title}
                        onChange={handleInputChange}
                        required
                        className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-400"
                        placeholder="Enter conference title"
                    />
                </div>

                <div>
                    <label htmlFor="description" className="block mb-2 font-medium">
                        Description
                    </label>
                    <textarea
                        id="description"
                        name="description"
                        value={formData.description}
                        onChange={handleInputChange}
                        required
                        className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-400 h-32"
                        placeholder="Enter conference description"
                    />
                </div>

                <div>
                    <label htmlFor="date" className="block mb-2 font-medium">
                        Conference Date
                    </label>
                    <input
                        type="date"
                        id="date"
                        name="date"
                        value={formData.date}
                        onChange={handleInputChange}
                        required
                        className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-400"
                    />
                </div>

                <div>
                    <label htmlFor="reviewers" className="block mb-2 font-medium">
                        Select Reviewers
                    </label>
                    <select
                        id="reviewers"
                        name="reviewers"
                        multiple
                        value={formData.reviewerIds}
                        onChange={handleReviewerChange}
                        className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-400 h-32"
                    >
                        {reviewers.map(reviewer => (
                            <option key={reviewer.id} value={reviewer.id}>
                                {reviewer.name || reviewer.email}
                            </option>
                        ))}
                    </select>
                    <p className="text-sm text-gray-500 mt-1">
                        Hold Ctrl (Cmd on Mac) to select multiple reviewers
                    </p>
                </div>

                <button
                    type="submit"
                    disabled={loading}
                    className={`w-full py-2 px-4 rounded ${
                        loading
                            ? 'bg-gray-400'
                            : 'bg-blue-500 hover:bg-blue-600'
                    } text-white font-medium`}
                >
                    {loading ? 'Creating...' : 'Create Conference'}
                </button>
            </form>
        </div>
    );
};

export default ConferenceCreate;
